css:
/*pc*/
@media (min-width: 601px){
.top-bar__countdown {background: #393d47;
  color: #ffffff;font-family: 'Lato',sans-serif;
    }
.top-bar__countdown {
    font-size: 12px;
    padding-top: var(--spacer-2);
    padding-bottom: var(--spacer-2);
}
.top-bar__nav {background: #393d47;font-family: 'Lato',sans-serif;
    max-width: 80rem; font-size: 12px;
    color: #ffffff;
    margin-left: auto;
    margin-right: auto;
    padding: var(--spacer-2) var(--spacer-2) var(--spacer-1);
}
.d-lg-inline {
    display: none!important;
}
.header{background: #ffffff;
     border-bottom: 1px solid #e7e7e7;
  box-shadow: var(--box-shadow-sm);}
.search-bar{ width:200px; margin-left:300px;}
.header__buttons .btn {
    font-size: 16px;
}
.header__buttons .btn .badge {
    position: absolute;
    top: 3px;
    right: -3px;
    font-size: 10px;
}
.header__search-btn {
    font-size: 18px;
    margin-bottom: calc(var(--spacer-2)*-1 + -.85rem);
}

  .header__logo {
    max-width: 300px;
    max-height: 100px;margin-left: 520px;

}
  .page-title__head{font-size: 1.2rem;
    font-weight: 300;
    text-transform: uppercase;
    text-align: center;}
  .lead {
    font-size: 0.75rem;
    font-weight: 300;font-family: 'Lato',sans-serif; }

  #search-bar {
    margin-top: 0px;
    margin-left: -620px;width:200px;
}
  .header__search-input {
    padding: 0 var(--spacer-3);
    width: 250px;font-weight: 300;font-family: 'Lato',sans-serif;
    font-size: 1rem}
  .header__search {
    position: absolute;
    overflow: hidden;
}
  .header__nav{font-size: 0.6rem;font-weight: 300;font-family: 'Lato',sans-serif;}
  .header__nav a:hover {
    background: transparent;font-size: 0.6rem;font-weight:500;font-family: 'Lato',sans-serif;
    text-decoration: none;}
}
  .product-card__offer-stamp {
    background-color: #a9b5d4;
    color: var(--success-yiq);
    min-width: 55px;
    font-size: 80%;
    line-height: 2.2;
    text-align: center;
    position: absolute;
    z-index: 9;
    top: 1rem;
    right: 0;
    border-radius: var(--border-radius);
    transition: border-radius .3s;
    opacity: .9;
}
  .btn-primary:not(:disabled):not(.disabled):active{color: #fff;
    background-color: #a9b5d4;
    border-color: #a9b5d4;font-family: 'Lato',sans-serif;
}.btn-primary {
    color: #fff;
    background-color: #a9b5d4;font-family: 'Lato',sans-serif;
    border-color: #a9b5d4;
}
  .prices__value {
    display: block;
    font-size: 1rem;font-family: 'Lato',sans-serif; font-weight: 300;
  }.product-card__name{font-weight: 400;font-size: 0.7rem;font-family: 'Lato',sans-serif;}
  .prices:not(.prices--literal) .prices__compare {font-weight: 400;font-size: 0.5rem;font-family: 'Lato',sans-serif;
    color: var(--gray);
}h1 {
    font-size: 1.6rem;font-weight: 400;font-family: 'Lato',sans-serif;
}.product__sku {
    color: var(--text-muted);
    font-size: 0.4rem;
}.prices--big {
    font-size: 0.8rem;
}
  .prices--big .prices__value {
    font-size: 1.5rem;font-weight: 400;font-family: 'Lato',sans-serif;color: #a9b5d4;
  }

.product__discount {
    color: #222222;font-size: 0.7rem;font-weight: 400;font-family: 'Lato',sans-serif;
}
.product__info{color: #222222;font-size: 0.8rem;font-weight: 400;font-family: 'Lato',sans-serif;
}
 .body{font-weight: 400;font-family: 'Lato',sans-serif;font-size: 0.8rem;
}
  .products-carousel__title{font-weight: 400;font-family: 'Lato',sans-serif;font-size: 1.2rem;
color:#a9b5d4;}
  .share-network-whatsapp{display:none;}
  .share-network-facebook{display:none;}
 .share-network-twitter{display:none;}
  .share-network-telegram{display:none;}
  .share-network-pinterest{display:none;}
  .share-network-email{display:none;}
  .share-network-sms {display:none;}
}
