css:
/*pc*/
@media (min-width: 601px){
.my-lg-5 {margin-top: 1rem;}
.product-card__offer-stamp {background-color: #3a3a40;border-radius: 40px;line-height: 3;opacity: 1.9;}
.product-card__offer-stamp i{font-size: 0%;opacity: 0;}
.footer {background: #f1f1f1;border-top: var(--spacer-1) solid #f8f9fa;}
.footer__store {color: #3a3a40;font-weight: var(--font-headings);}
.category-retail{width:75% important!;}
.top-bar__countdown {background: #393d47; color: #ffffff;font-family: 'Jost', sans-serif;}
.top-bar__page-links { display: inline-block;margin-left: 1000px;}
.top-bar__countdown {font-size: 16px;padding-top: var(--spacer-2);padding-bottom: var(--spacer-2);}
.top-bar__nav {
    background: #ffffff;font-family: 'Jost', sans-serif;max-width: 80rem;
    font-size: 14px; color: #3a3a40;margin-left: auto; margin-right: auto;
    padding: var(--spacer-2) var(--spacer-2) var(--spacer-1);}
.d-lg-inline {display: none!important;}
.header{background: #ffffff;height: 140px;border-bottom: 1px solid #e7e7e7;
  box-shadow: var(--box-shadow-sm);}
.search-bar{ width:200px; margin-left:300px;}
.header__buttons .btn {font-size: 20px;color:#393d47;}
 .hero-banner {margin: 0px;}
.header__buttons .btn .badge {position: absolute;top: 3px;right: -3px;font-size: 10px;}
.header__search-btn {font-size: 18px;color: #393d47;margin-bottom: calc(var(--spacer-2)*-1 + -.85rem);}
.header__logo {max-width: 300px;margin-top: -20px;
    max-height: 100px;margin-left: 530px;}
.page-title__head{font-size: 1.2rem; font-weight: 300;font-family: 'Jost', sans-serif;
