#search-bar { margin-top: -60px;margin-left: -620px;width:200px;}
.mt-lg-0{margin-top: -30px;}
.header__search-input {
    padding-top: 30px;border-bottom: 1px solid #393d47; border-radius:0px;
    width: 250px;font-weight: 500;font-family: 'Jost', sans-serif;
    font-size:0.9rem}
.header__search {position: absolute;overflow: hidden;}
.header__nav{font-size: 0.8rem;font-weight: 300;font-family: 'Jost', sans-serif;margin-top: 10
px;}.header__nav a {font-family: 'Jost', sans-serif;
    text-transform: uppercase;
  font-weight: 300;}
.header__nav a:hover {
    background: transparent;font-size: 0.8rem;font-weight:400;font-family: 'Jost', sans-serif;
    text-decoration: none;}
  .menu__list a:hover {
    background: transparent;font-size: 0.6rem;font-weight:500;font-family: 'Jost', sans-serif;
    text-decoration: none;}
  .product__short-stock{display: none;}
  .header__toggler-icon {
    color: #3a3a40;}
.menu__item{font-size: 0.6rem;font-weight: 400;font-family: 'Jost', sans-serif;}
.menu__item a:hover { background: transparent;font-size: 0.6rem;font-weight:600;font-family: 'Jost', sans-serif;
    text-decoration: none;}
  .product-card__offer-stamp {
    background-color: #393d47;
    color: var(--success-yiq);
    min-width: 55px;
    font-size: 80%;
    text-align: center;
    position: absolute;
    z-index: 9;
    top: 1rem;
    right: 0;
    border-radius: 80px;
    transition: border-radius .3s;
    opacity: .9;
}
  .ecom-credits {
        background: #e3e3e3;}
  .btn-primary:not(:disabled):not(.disabled):active{color: #fff;
    background-color: #393d47;
    border-color: #393d47;font-family: 'Jost', sans-serif;
}.btn-primary {
    color: #fff;
     background-color: #393d47;font-family: 'Jost', sans-serif;
    border-color: #393d47;
}
  .html-clearfix{border: 0px solid #ccc;
    padding: 20px 20px 20px 20px;font-size: 0.7rem;
    font-weight: 300;
    font-family: 'Jost', sans-serif;
}
  .prices__value {
    display: block;
    font-size: 1rem;font-family: 'Jost', sans-serif;font-weight: 500;
  }.product-card__name{font-weight: 400;font-size: 0.7rem;font-family: 'Jost', sans-serif;}
  .prices:not(.prices--literal) .prices__compare {font-weight: 400;font-size: 0.5rem;font-family: 'Jost', sans-serif;
    color: var(--gray);
}h1 {color: #393d47;
    font-size: 1.0rem;font-weight: 400;font-family: 'Jost', sans-serif;
}.product__sku {
    color: var(--text-muted);
    font-size: 0.4rem;
}.prices--big {
    font-size: 0.8rem;
}
  .prices--big .prices__value {
    font-size: 1.5rem;font-weight: 400;font-family: 'Jost', sans-serif;color: #a9b5d4;
  }

.product__discount {
    color: #222222;font-size: 0.7rem;font-weight: 400;font-family: 'Jost', sans-serif;
}
.product__info{color: #222222;font-size: 0.8rem;font-weight: 400;font-family: 'Jost', sans-serif;
}
 .body{font-weight: 400;font-family: 'Jost', sans-serif;font-size: 0.6rem;
}
  .products-carousel__title{font-weight: 400;font-family: 'Jost', sans-serif;font-size: 1.2rem;
color:#a9b5d4;}
  .search-engine__nav { font-family: 'Jost', sans-serif; font-weight:300; font-size: 0.6rem;}
  .search-engine__toggles button{ font-size: 0.5rem;font-family: 'Jost', sans-serif;
}
  .h5 {
    font-size: 0.7rem;
}
  .card-header{ font-size: 0.6rem;}
  .card-body{font-size: 0.7rem;font-family: 'Jost', sans-serif;}
  .search-engine__filter button{font-size: 0.9rem;}
  .share-network-whatsapp{display:none;}
  .share-network-facebook{display:none;}
 .share-network-twitter{display:none;}
  .share-network-telegram{display:none;}
